mapper.select:
          select    
		      from tab_usrinf_2 


		         usrinf_id =  #{usrinf_id}


			    and usrid =  #{usrid}


			    and is_store =  #{is_store}


			    and is_activate = #{is_activate}


			    and usrname like CONCAT('%', #{usrname}, '%')


			    and id_card like CONCAT('%', #{id_card}, '%')


			    and mobile_phone like CONCAT('%', #{mobile_phone}, '%')


		       and check_flag =  #{check_flag}


	  	      and applyfor_time >= #{applyfor_time_start}


			    and applyfor_time <= #{applyfor_time_end}


			    and check_flag != 0


		    order by applyfor_time desc

		     ${pager.orderCondition}


		       ${pager.mysqlQueryCondition}

	select count(1) from tab_usrinf_2


	         usrinf_id =  #{usrinf_id}


		    and usrid =  #{usrid}


		    and is_store =  #{is_store}


		    and is_activate = #{is_activate}


		    and usrname like CONCAT('%', #{usrname}, '%')


		    and id_card like CONCAT('%', #{id_card}, '%')


		    and mobile_phone like CONCAT('%', #{mobile_phone}, '%')


	       and check_flag =  #{check_flag}


  	      and applyfor_time >= #{applyfor_time_start}


		    and applyfor_time <= #{applyfor_time_end}


		    and check_flag != 0


    SELECT
       a.*,
       b.order_sn,
       b.store_charge_rate,
       b.order_money
    FROM
       tab_usrinf_2 a
    JOIN
       tab_usr_order b
    ON
       a.borrower_id_card = b.borrower_id_card


	         a.usrid =  #{usrid}


	         a.borrower_usrname =  #{borrower_usrname}


		    and a.borrower_mobile_phone =  #{borrower_mobile_phone}


		    and b.order_sn =  #{order_sn}


		    and a.is_store =  #{is_store}


		    and b.store_uid =  #{store_uid}


		    and b.store_name =  #{store_name}


		    and b.store_contract =  #{store_contract}


		    and b.store_tel =  #{store_tel}


		    and UNIX_TIMESTAMP(b.create_time) >= #{start_time}


		    and #{end_time} > UNIX_TIMESTAMP(b.create_time)


	 order by a.usrid asc

	     ${pager.orderCondition}


	       ${pager.mysqlQueryCondition}


